import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { API_ENDPOINTS } from '../config/api';

function OrderList() {
  const [orders, setOrders] = useState([]);

  useEffect(() => {
    fetchOrders();
  }, []);

  const fetchOrders = async () => {
    try {
      const response = await axios.get(API_ENDPOINTS.ORDERS);
      console.log('Orders data:', response.data);
      setOrders(Array.isArray(response.data) ? response.data : []);
    } catch (error) {
      console.error('Error fetching orders:', error);
      setOrders([]);
    }
  };

  return (
    <div className="ml-64 p-8">
      <div className="max-w-6xl mx-auto">
        <h1 className="text-3xl font-bold text-[#2E856E] mb-8">Orders</h1>
        <div className="bg-white rounded-2xl shadow-lg overflow-hidden">
          <div className="grid grid-cols-5 gap-4 bg-[#98DBBC] p-4 font-semibold text-[#2E856E]">
            <div>Order ID</div>
            <div>Customer</div>
            <div>Items</div>
            <div>Total</div>
            <div>Date</div>
          </div>
          <div className="divide-y divide-gray-100">
            {orders.map((order) => (
              <div
                key={order.id}
                className="grid grid-cols-5 gap-4 p-4 hover:bg-gray-50 transition-colors"
              >
                <div className="text-[#2E856E] font-medium">#{order.id}</div>
                <div className="font-medium">{order.customer_name}</div>
                <div className="text-gray-600">
                  {order.items ? (
                    typeof order.items === 'string' ? (
                      order.items
                    ) : (
                      Array.isArray(order.items) &&
                      order.items.map((item, index) => (
                        <div key={index} className="text-sm">
                          {item.product_id?.product_name || 'Unknown Product'} x {item.quantity}
                        </div>
                      ))
                    )
                  ) : 'No items'}
                </div>
                <div className="font-bold text-[#2E856E]">
                  Rp {order.total_amount?.toLocaleString()}
                </div>
                <div className="text-gray-600">
                  {new Date(order.created_at).toLocaleDateString('id-ID', {
                    year: 'numeric',
                    month: 'long',
                    day: 'numeric'
                  })}
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}

export default OrderList;
