import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Container,
  TextField,
  Button,
  Grid,
  Typography,
  Select,
  MenuItem,
  FormControl,
  InputLabel,
  Box,
} from '@mui/material';
import axios from 'axios';
import { API_ENDPOINTS } from '../config/api';

function OrderForm() {
  const navigate = useNavigate();
  const [products, setProducts] = useState([]);
  const [order, setOrder] = useState({
    customer_name: '',
    items: [{ product_id: '', quantity: 1 }]
  });

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    try {
      const response = await axios.get(API_ENDPOINTS.PRODUCTS);
      setProducts(response.data);
    } catch (error) {
      console.error('Error fetching products:', error);
    }
  };

  const handleChange = (e) => {
    setOrder({
      ...order,
      [e.target.name]: e.target.value
    });
  };

  const handleItemChange = (index, field, value) => {
    const newItems = [...order.items];
    newItems[index] = {
      ...newItems[index],
      [field]: value
    };
    setOrder({
      ...order,
      items: newItems
    });
  };

  const addItem = () => {
    setOrder({
      ...order,
      items: [...order.items, { product_id: '', quantity: 1 }]
    });
  };

  const removeItem = (index) => {
    const newItems = order.items.filter((_, i) => i !== index);
    setOrder({
      ...order,
      items: newItems
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // Pastikan semua item memiliki product_id yang valid
      const isValid = order.items.every(item => item.product_id && item.quantity > 0);
      if (!isValid) {
        alert('Please select a product and quantity for all items');
        return;
      }

      console.log('Submitting order:', order);
      
      // Cek koneksi ke server
      try {
        await axios.get(API_ENDPOINTS.ORDERS);
      } catch (error) {
        if (error.code === 'ERR_NETWORK') {
          alert('Cannot connect to server. Please make sure the backend server is running.');
          return;
        }
      }

      const response = await axios.post(API_ENDPOINTS.ORDERS, order);
      console.log('Order created successfully:', response.data);
      navigate('/orders');
    } catch (error) {
      console.error('Error creating order:', error);
      console.error('Error response:', error.response?.data);
      
      if (error.code === 'ERR_NETWORK') {
        alert('Cannot connect to server. Please make sure the backend server is running.');
      } else {
        alert('Failed to create order. Please try again. Error: ' + (error.response?.data?.message || error.message));
      }
    }
  };

  return (
    <Container sx={{ mt: 4 }}>
      <Typography variant="h4" gutterBottom>
        Create New Order
      </Typography>
      <form onSubmit={handleSubmit}>
        <Grid container spacing={3}>
          <Grid item xs={12}>
            <TextField
              required
              fullWidth
              label="Customer Name"
              name="customer_name"
              value={order.customer_name}
              onChange={handleChange}
            />
          </Grid>
          
          {order.items.map((item, index) => (
            <Grid item xs={12} key={index}>
              <Box sx={{ display: 'flex', gap: 2, alignItems: 'center' }}>
                <FormControl fullWidth>
                  <InputLabel>Product</InputLabel>
                  <Select
                    value={item.product_id}
                    onChange={(e) => handleItemChange(index, 'product_id', e.target.value)}
                    label="Product"
                    required
                  >
                    <MenuItem value="">
                      <em>Select a product</em>
                    </MenuItem>
                    {products.map((product) => (
                      <MenuItem key={product.id} value={product.id}>
                        {product.product_name} - Rp {product.product_price}
                      </MenuItem>
                    ))}
                  </Select>
                </FormControl>
                <TextField
                  type="number"
                  label="Quantity"
                  value={item.quantity}
                  onChange={(e) => handleItemChange(index, 'quantity', parseInt(e.target.value) || 1)}
                  sx={{ width: '120px' }}
                  inputProps={{ min: 1 }}
                  required
                />
                <Button
                  variant="outlined"
                  color="error"
                  onClick={() => removeItem(index)}
                  disabled={order.items.length === 1}
                >
                  Remove
                </Button>
              </Box>
            </Grid>
          ))}

          <Grid item xs={12}>
            <Button
              variant="outlined"
              onClick={addItem}
              sx={{ mr: 2 }}
            >
              Add Item
            </Button>
            <Button
              type="submit"
              variant="contained"
              color="primary"
            >
              Create Order
            </Button>
          </Grid>
        </Grid>
      </form>
    </Container>
  );
}

export default OrderForm;
