import React, { useState, useEffect } from 'react';
import { Typography } from '@mui/material';
import axios from 'axios';
import { API_ENDPOINTS } from '../config/api';

function OrderList() {
  const [orders, setOrders] = useState([]);

  useEffect(() => {
    fetchOrders();
  }, []);

  const fetchOrders = async () => {
    try {
      const response = await axios.get(API_ENDPOINTS.ORDERS);
      console.log('Orders data:', response.data);
      setOrders(Array.isArray(response.data) ? response.data : []);
    } catch (error) {
      console.error('Error fetching orders:', error);
      setOrders([]);
    }
  };

  return (
    <div className="ml-64 p-8">
      <div className="max-w-4xl mx-auto">
        <Typography variant="h4" className="text-indigo-900 font-bold mb-6">
          Orders
        </Typography>
        <div className="bg-white rounded-2xl shadow-xl overflow-hidden">
          <div className="grid grid-cols-5 gap-4 bg-indigo-50 p-4 font-semibold text-indigo-900">
            <div>Order ID</div>
            <div>Customer</div>
            <div>Items</div>
            <div>Total</div>
            <div>Date</div>
          </div>
          {orders.map((order) => (
            <div
              key={order.id}
              className="grid grid-cols-5 gap-4 p-4 border-b hover:bg-gray-50 transition-colors"
            >
              <div>{order.id}</div>
              <div>{order.customer_name}</div>
              <div>
                {order.items ? (
                  typeof order.items === 'string' ? (
                    order.items
                  ) : (
                    Array.isArray(order.items) &&
                    order.items.map((item, index) => (
                      <div key={index} className="text-sm">
                        {item.product_id?.product_name || 'Unknown Product'} x {item.quantity}
                      </div>
                    ))
                  )
                ) : 'No items'}
              </div>
              <div>Rp {order.total_amount}</div>
              <div>{new Date(order.created_at).toLocaleDateString()}</div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default OrderList;
