import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  TextField,
  Button,
  Typography,
  Select,
  MenuItem,
  FormControl,
  InputLabel,
} from '@mui/material';
import axios from 'axios';
import { API_ENDPOINTS } from '../config/api';

function OrderForm() {
  const navigate = useNavigate();
  const [products, setProducts] = useState([]);
  const [order, setOrder] = useState({
    customer_name: '',
    items: [{ product_id: '', quantity: 1 }],
  });

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    try {
      const response = await axios.get(API_ENDPOINTS.PRODUCTS);
      setProducts(response.data);
    } catch (error) {
      console.error('Error fetching products:', error);
    }
  };

  const handleChange = (e) => {
    setOrder({
      ...order,
      [e.target.name]: e.target.value,
    });
  };

  const handleItemChange = (index, field, value) => {
    const newItems = [...order.items];
    newItems[index] = {
      ...newItems[index],
      [field]: value,
    };
    setOrder({
      ...order,
      items: newItems,
    });
  };

  const addItem = () => {
    setOrder({
      ...order,
      items: [...order.items, { product_id: '', quantity: 1 }],
    });
  };

  const removeItem = (index) => {
    const newItems = order.items.filter((_, i) => i !== index);
    setOrder({
      ...order,
      items: newItems,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const isValid = order.items.every(item => item.product_id && item.quantity > 0);
      if (!isValid) {
        alert('Please select a product and quantity for all items');
        return;
      }

      console.log('Submitting order:', order);
      try {
        await axios.get(API_ENDPOINTS.ORDERS);
      } catch (error) {
        if (error.code === 'ERR_NETWORK') {
          alert('Cannot connect to server. Please make sure the backend server is running.');
          return;
        }
      }

      const response = await axios.post(API_ENDPOINTS.ORDERS, order);
      console.log('Order created successfully:', response.data);
      navigate('/orders');
    } catch (error) {
      console.error('Error creating order:', error);
      console.error('Error response:', error.response?.data);
      if (error.code === 'ERR_NETWORK') {
        alert('Cannot connect to server. Please make sure the backend server is running.');
      } else {
        alert('Failed to create order. Please try again. Error: ' + (error.response?.data?.message || error.message));
      }
    }
  };

  return (
    <div className="ml-64 p-8">
      <div className="max-w-3xl mx-auto">
        <Typography variant="h4" className="text-[#88D8C0] font-bold mb-6">
          Create New Order
        </Typography>
        <div className="space-y-6">
          <TextField
            required
            fullWidth
            label="Customer Name"
            name="customer_name"
            value={order.customer_name}
            onChange={handleChange}
            className="bg-gray-50 rounded-lg"
            InputProps={{
              className: 'border-gray-300 focus:border-[#88D8C0]',
            }}
          />
          {order.items.map((item, index) => (
            <div key={index} className="flex items-center space-x-4 bg-gray-50 p-4 rounded-lg">
              <FormControl className="flex-1">
                <InputLabel>Product</InputLabel>
                <Select
                  value={item.product_id}
                  onChange={(e) => handleItemChange(index, 'product_id', e.target.value)}
                  label="Product"
                  required
                  className="bg-white rounded-lg"
                >
                  <MenuItem value="">
                    <em>Select a product</em>
                  </MenuItem>
                  {products.map((product) => (
                    <MenuItem key={product.id} value={product.id}>
                      {product.product_name} - Rp {product.product_price}
                    </MenuItem>
                  ))}
                </Select>
              </FormControl>
              <TextField
                type="number"
                label="Qty"
                value={item.quantity}
                onChange={(e) => handleItemChange(index, 'quantity', parseInt(e.target.value) || 1)}
                className="w-24 bg-white rounded-lg"
                inputProps={{ min: 1 }}
                required
              />
              <Button
                onClick={() => removeItem(index)}
                disabled={order.items.length === 1}
                className="text-red-600 hover:bg-red-100 rounded-lg px-4 py-2"
              >
                Remove
              </Button>
            </div>
          ))}
          <div className="flex space-x-4">
            <Button
              onClick={addItem}
              className="bg-[#B3E5D6] text-white hover:bg-[#B3E5D6] rounded-full px-6 py-2"
            >
              Add Item
            </Button>
            <Button
              onClick={handleSubmit}
              className="bg-[#4CAF50] text-white hover:bg-[#4CAF50] rounded-full px-6 py-2"
            >
              Create Order
            </Button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default OrderForm;
